mapper.select:
SELECT * FROM (SELECT ROWNUM R, L.* FROM (SELECT * FROM LECTURE L JOIN L_FILE LF ON L.L_NUM = LF.L_NUM JOIN VIDEO VV ON L.L_NUM = VV.L_NUM JOIN "LEVEL" LV ON L.LEVEL_NUM = LV.LEVEL_NUM JOIN CATEGORY C ON L.C_NUM = C.C_NUM WHERE
L.ID L.L_NAME LV.LEVEL_NAME C.C_NAME
LIKE '%'||#{search}||'%' ORDER BY L.L_NUM DESC) L) WHERE R BETWEEN #{startRow} AND #{lastRow}

mapper.update:
    		UPDATE LECTURE SET 
    		LEVEL_NUM = #{level_num}, L_NAME = #{l_name}, L_CONTENTS = #{l_contents},
    		C_NUM = #{c_num}, L_PRICE = #{l_price}, L_DATE = #{l_date}
			WHERE L_NUM=#{l_num}
